package com.sphereon.crypto


import com.sphereon.crypto.cose.CoseKeyCbor
import kotlinx.coroutines.test.TestResult
import kotlinx.coroutines.test.runTest
import kotlin.test.Test

class X509CertificateServiceTest {

    @OptIn(ExperimentalStdlibApi::class)
    @Test
    fun shouldValidateCert(): TestResult {

        return runTest {
            println("Running verifyCertChain Common test")
            CryptoService.X509.disable() // Disable here, as we do not have a JVM implementation yet. There is a separate JS test
            val result = CryptoService.X509.verifyCertificateChain<CoseKeyCbor>(
                chainDER = arrayOf("".hexToByteArray()),
                chainPEM = arrayOf("chainpem1", "chainpem2"), trustedCerts = arrayOf(
                    "chainpem1"
                )
            )
            println("Done verifyCertChain test:")
            println(result.toString())
        }

    }
}
