package com.sphereon.crypto


import kotlinx.coroutines.test.TestResult
import kotlinx.coroutines.test.runTest
import x509Service
import kotlin.test.Test

class X509CertificateServiceJS {

    @OptIn(ExperimentalStdlibApi::class)
    @Test
    fun shouldValidateCert(): TestResult {

        return runTest {
            println("Running verifyCertChain test")
            val result = x509Service().verifyCertificateChain(
                chainDER = arrayOf("".hexToByteArray()),
                chainPEM = arrayOf("chainpem1", "chainpem2"), trustedPEM = arrayOf(
                    "chainpem1"
                )
            )
            println("Done verifyCertChain test:")
            println(result.toString())
        }

    }
}
